package constants

import (
	"github.com/qdm12/gluetun/internal/models"
)

//nolint:lll
const (
	IvpnCA                 = ""
	IvpnOpenvpnStaticKeyV1 = ""
)

func IvpnCountryChoices() (choices []string) {
	servers := IvpnServers()
	choices = make([]string, len(servers))
	for i := range servers {
		choices[i] = servers[i].Country
	}
	return makeUnique(choices)
}

