package expressvpn

import (
	"github.com/qdm12/gluetun/internal/configuration/settings"
	"github.com/qdm12/gluetun/internal/constants/openvpn"
	"github.com/qdm12/gluetun/internal/models"
	"github.com/qdm12/gluetun/internal/provider/utils"
)

func (p *Provider) OpenVPNConfig(connection models.Connection,
	settings settings.OpenVPN) (lines []string) {
	//nolint:gomnd
	providerSettings := utils.OpenVPNProviderSettings{
		RemoteCertTLS: true,
		AuthUserPass:  true,
		Ciphers: []string{
			openvpn.AES256gcm, openvpn.AES256cbc, openvpn.AES128gcm,
		},
		Auth:           openvpn.SHA512,
		CA:             "", //nolint:lll
		Cert:           "",                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             //nolint:lll
		RSAKey:         "",                                                                                                                                                                                                                                                                                                                                                                                                                                                                     //nolint:lll
		TLSAuth:        "",                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             //nolint:lll
		MssFix:         1200,
		FastIO:         true,
		Fragment:       1300,
		SndBuf:         524288,
		RcvBuf:         524288,
		KeyDirection:   "1",
		VerifyX509Type: "name-prefix",
		// Always verify against `Server` x509 name prefix, security hole I guess?
		VerifyX509Name: "Server",
	}
	return utils.OpenVPNConfig(providerSettings, connection, settings)
}
