package privateinternetaccess

import (
	"github.com/qdm12/gluetun/internal/configuration/settings"
	"github.com/qdm12/gluetun/internal/constants/openvpn"
	"github.com/qdm12/gluetun/internal/models"
	"github.com/qdm12/gluetun/internal/provider/privateinternetaccess/presets"
	"github.com/qdm12/gluetun/internal/provider/utils"
)

func (p *Provider) OpenVPNConfig(connection models.Connection,
	settings settings.OpenVPN, ipv6Supported bool) (lines []string) {
	providerSettings := utils.OpenVPNProviderSettings{
		RemoteCertTLS: true,
		RenegDisabled: true,
		AuthUserPass:  true,
	}

	const (
		caNormalPreset        = "" //nolint:lll
		crlVerifyNormalPreset = ""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             //nolint:lll
	)
	switch *settings.PIAEncPreset {
	case presets.Normal:
		providerSettings.Ciphers = []string{openvpn.AES128cbc}
		providerSettings.Auth = openvpn.SHA1
		providerSettings.CRLVerify = crlVerifyNormalPreset
		providerSettings.CA = caNormalPreset
	case presets.None:
		providerSettings.Ciphers = []string{"none"}
		providerSettings.Auth = "none"
		providerSettings.CRLVerify = crlVerifyNormalPreset
		providerSettings.CA = caNormalPreset
	default: // strong
		providerSettings.Ciphers = []string{openvpn.AES256cbc}
		providerSettings.Auth = openvpn.SHA256
		providerSettings.CRLVerify = ""                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              //nolint:lll
		providerSettings.CA = "" //nolint:lll
	}

	return utils.OpenVPNConfig(providerSettings, connection, settings, ipv6Supported)
}
